definition.script:
pipeline {
    parameters {
        string name: 'BUILD_MACHINE', defaultValue: 'centos', description: 'Build machine', trim: true
        string defaultValue: 'echo "Build command not specified"', description: '', name: 'BUILD_CMD', trim: true
        string defaultValue: '', description: '', name: 'GIT_URL', trim: true
        string defaultValue: 'master', name: 'BRANCH', description: '', trim: true
        string defaultValue: '0.0.0', name: 'VERSION', description: '', trim: true
        string defaultValue: 'server', name: 'TYPE', description: '', trim: true
    }
    stages {
        stage('Clean work directory') {
            agent { label "${BUILD_MACHINE}" }
            steps {
                sh 'rm -rf *'
                sh 'test -d .git || rm -rf .git'
            }
        }
        stage('Get sources') {
            agent { label "${BUILD_MACHINE}" }
            steps {
                git branch: "${BRANCH}", credentialsId: 'git_passwd', url: "${GIT_URL}"
            }
        }
        stage('Build') {
            agent { label "${BUILD_MACHINE}" }
            steps {
                sh "$BUILD_CMD"
            }
        }
        stage('Copy to storage') {
            agent { label "${BUILD_MACHINE}" }
            steps {
                sh "sshpass -p \"34appterr21\" ssh jenkins@10.10.199.31 \"test -d /ftp/releases/RosChat/server/${JOB_NAME} || mkdir /ftp/releases/RosChat/server/${JOB_NAME}/\""
                sh "sshpass -p \"34appterr21\" rsync -r -e ssh ${WORKSPACE}/result/* jenkins@10.10.199.31:/ftp/releases/RosChat/${TYPE}/${JOB_NAME}/\$(date +'%d.%m.%Y_%H:%M')_${VERSION}-${BUILD_NUMBER}/"
            }
        }
    }
}
